  def StringLoremIpsumGenerator.word(number)
    return @@lorem
    .split(' ')
    .slice(0, number)
    .join(' ');
  end
  
  def StringLoremIpsumGenerator.paragraph(number) 
    return @@lorem
      .split("\n")
      .slice(0, number)
      .join("\n");
    end
